import React from 'react'
import { Card, Button } from 'react-bootstrap'

class RecCourseBuilder extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            collapseStatus: false
        }
        this.select = React.createRef()
    }

    collapseControl() {

        let caption = 'View';
        // let variant = "info";

        let buttonText = '▼';
        let modalClick = () => this.collapseModal(true);
        let changeVariant = () => this.setVariant("parimary")

        if(this.state.collapseStatus) {
          buttonText = '▲';
          modalClick = () => this.collapseModal(false)
        }

        return (
          <Button variant='outline-dark' style={{width: 25, height: 25, fontSize: 12, padding: 0, position: 'absolute', right: 20, top: 20}} onClick={modalClick}>{buttonText}</Button>
        )
      }

      collapseModal(value) {
        this.setState({collapseStatus: value});
      }

      render() {
          const { data } = this.props

          return (
            <Card style={{width: '50%', margin: '5px auto'}}>
                <Card.Body>
                    <Card.Title>
                        <div style={{maxWidth: 250}}>
                        {data.name}
                        </div>
                        {this.collapseControl()}
                    </Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">{data.number} - {data.credits} Credits</Card.Subtitle>
                    {
                        this.state.collapseStatus === true && (
                            <div>
                                {data.description}
                            </div>
                        )
                    }
                </Card.Body>
            </Card>
          )
      }
}

export default RecCourseBuilder
