import React from 'react'
import { Card, Button, Form } from 'react-bootstrap'

class PreCourseBuilder extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            collapseStatus: false,
            variant: []
        }
        this.select = React.createRef()
    }

    collapseControl() {
        let caption = 'View';
        // let variant = "info";
        let modalClick = () => this.collapseModal(true);
        let changeVariant = () => this.setVariant("parimary")

        if(this.state.collapseStatus) {
            caption = 'Hide';
          changeVariant = () => this.setVariant("danger")
          modalClick = () => this.collapseModal(false)
        }

        return (
          <Button 
          variant= "danger" 
          style={{width: 55, height: 45, fontSize: 20, padding: 0, position: 'absolute', right: 20, top: 20}} 
          onClick={modalClick}><b>{caption}</b></Button>
        )

      }

      collapseModal(value) {
        this.setState({collapseStatus: value});
      }

      setVariant(color) {
          this.setState({variant: color })
      }

      render() {

          const { data } = this.props

          return (
            <Card style={{width: '50%', margin: '5px auto'}}>
                <Card.Body>
                    <Card.Title>
                        <div style={{maxWidth: 250}}>
                        {data.name}
                        </div>
                        {this.collapseControl()}
                    </Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">{data.number} - {data.credits} Credits</Card.Subtitle>
                    {
                        this.state.collapseStatus === true && (
                            <div>
                                {data.description}
                            </div>
                        )
                    }
                    <Form>
                        <Form.Group>
                            <Form.Control ref={this.select} as="select" onClick={() => {this.props.onSel(this.select.current.value, data)}}>
                                <option key="No Rating">No Rating</option>
                                <option key="1">1</option>
                                <option key="2">2</option>
                                <option key="3">3</option>
                                <option key="4">4</option>
                                <option key="5">5</option>
                            </Form.Control>
                        </Form.Group>
                    </Form>
                </Card.Body>
            </Card>
          )
      }
}

export default PreCourseBuilder
